mapper.select:
select

from freezen_bill
where bankid = #{bankid,jdbcType=VARCHAR}
order by ctime desc
limit #{offset},#{limit}

select

from freezen_bill
where bankid = #{bankid,jdbcType=VARCHAR}
and `year` = #{year,jdbcType=INTEGER}
and `month` = #{month,jdbcType=INTEGER}
order by ctime desc
limit #{offset},#{limit}

select
IFNULL(sum(amount),0)
from freezen_bill
where bankid = #{bankid,jdbcType=VARCHAR}
and `year` = #{year,jdbcType=INTEGER}
and month =#{month}
and `order`=0

select
IFNULL(sum(amount),0)
from freezen_bill
where bankid = #{bankid,jdbcType=VARCHAR}
and `year` = #{year,jdbcType=INTEGER}
and month =#{month}
and (`order`=1 or `order`=2)
